 if (configContent.isBlank()) {
 Log.w("ServerConfig", "Config file is empty, using fallback")
 "http://192.168.1.101:5000/"
 } else {
 "http://$configContent/"
 }

 } catch (e: Exception) {
 Log.e("ServerConfig", "Error loading server config", e)
 val fallback = "http://192.168.1.101:5000/"
 Log.d("ServerConfig", "Using fallback config: $fallback")
 fallback
 }
 }
