package com.example.my_fridge_android.ui.login

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.my_fridge_android.ui.login.LoginContract.UiAction
import com.example.my_fridge_android.ui.login.LoginContract.UiEffect
import com.example.my_fridge_android.ui.login.LoginContract.UiState
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.channels.Channel
import kotlinx.coroutines.delay
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.flow.receiveAsFlow
import kotlinx.coroutines.flow.update
import kotlinx.coroutines.launch
import javax.inject.Inject

@HiltViewModel
class LoginViewModel @Inject constructor() : ViewModel() {

 private val _uiState = MutableStateFlow(UiState())
 val uiState: StateFlow<UiState> = _uiState.asStateFlow()

 private val _uiEffect by lazy { Channel<UiEffect>() }
 val uiEffect: Flow<UiEffect> by lazy { _uiEffect.receiveAsFlow() }

 fun onAction(uiAction: UiAction) {
 when (uiAction) {
 is UiAction.EmailChanged -> {
 updateUiState {
 copy(
 email = uiAction.email,
 isEmailError = false,
 emailErrorMessage = "",
 isLoginEnabled = validateLoginForm(uiAction.email, _uiState.value.password)
 )
 }
 }

 is UiAction.PasswordChanged -> {
 updateUiState {
 copy(
 password = uiAction.password,
 isPasswordError = false,
 passwordErrorMessage = "",
 isLoginEnabled = validateLoginForm(_uiState.value.email, uiAction.password)
 )
 }
 }

 is UiAction.TogglePasswordVisibility -> {
 updateUiState { copy(isPasswordVisible = !isPasswordVisible) }
 }

 is UiAction.LoginClicked -> {
 performLogin()
 }

 is UiAction.ForgotPasswordClicked -> {
 viewModelScope.launch {
 emitUiEffect(UiEffect.NavigateToForgotPassword)
 }
 }

 is UiAction.SignUpClicked -> {
 viewModelScope.launch {
 emitUiEffect(UiEffect.NavigateToSignUp)
 }
 }
 }
 }

 private fun validateLoginForm(email: String, password: String): Boolean {
 return email.isNotBlank() &&
 email.contains("@") &&
 password.isNotBlank() &&
 password.length >= 6
 }

 private fun performLogin() {
 val currentState = _uiState.value

 // Validate inputs
 var hasErrors = false

 if (currentState.email.isBlank()) {
 updateUiState {
 copy(
 isEmailError = true,
 emailErrorMessage = "Email is required"
 )
 }
 hasErrors = true
 } else if (!currentState.email.contains("@")) {
 updateUiState {
 copy(
 isEmailError = true,
 emailErrorMessage = "Please enter a valid email"
 )
 }
 hasErrors = true
 }

 if (currentState.password.isBlank()) {
 updateUiState {
 copy(
 isPasswordError = true,
 passwordErrorMessage = "Password is required"
 )
 }
 hasErrors = true
 } else if (currentState.password.length < 6) {
 updateUiState {
 copy(
 isPasswordError = true,
 passwordErrorMessage = "Password must be at least 6 characters"
 )
 }
 hasErrors = true
 }

 if (hasErrors) return

 // Simulate login process
 viewModelScope.launch {
 updateUiState { copy(isLoading = true) }

 try {
 // Simulate network delay
 delay(2000)

 // For demo purposes, accept any valid email/password
 emitUiEffect(UiEffect.NavigateToHome)
 } catch (e: Exception) {
 emitUiEffect(UiEffect.ShowError("Login failed. Please try again."))
 } finally {
 updateUiState { copy(isLoading = false) }
 }
 }
 }

 private fun updateUiState(block: UiState.() -> UiState) {
 _uiState.update(block)
 }

 private suspend fun emitUiEffect(uiEffect: UiEffect) {
 _uiEffect.send(uiEffect)
 }
}

