fun LoginScreen(
 uiState: UiState,
 uiEffect: Flow<UiEffect>,
 onAction: (UiAction) -> Unit,
 onNavigateToHome: () -> Unit = {},
 onNavigateToSignUp: () -> Unit = {},
 onNavigateToForgotPassword: () -> Unit = {},
) {
 var isVisible by remember { mutableStateOf(false) }

 LaunchedEffect(Unit) {
 delay(100)
 isVisible = true
 }

 // Handle UI effects
 LaunchedEffect(uiEffect) {
 uiEffect.collect { effect ->
 when (effect) {
 is UiEffect.NavigateToHome -> onNavigateToHome()
 is UiEffect.NavigateToSignUp -> onNavigateToSignUp()
 is UiEffect.NavigateToForgotPassword -> onNavigateToForgotPassword()
 is UiEffect.ShowError -> {
 // Handle error display if needed
 }
 }
 }
 }

 Box(
 modifier = Modifier
 .fillMaxSize()
 .background(
 brush = Brush.verticalGradient(
 colors = listOf(
 MaterialTheme.colorScheme.primaryContainer.copy(alpha = 0.3f),
 MaterialTheme.colorScheme.surface
 )
 )
 )
 ) {
 if (uiState.isLoading) {
 LoadingBar()
 } else {
 AnimatedVisibility(
 visible = isVisible,
 enter = fadeIn(animationSpec = tween(800)) + slideInVertically(
 animationSpec = spring(
 dampingRatio = Spring.DampingRatioMediumBouncy,
 stiffness = Spring.StiffnessLow
 ),
 initialOffsetY = { it / 2 }
 ),
 exit = fadeOut(animationSpec = tween(400)) + slideOutVertically()
 ) {
 LoginContent(
 uiState = uiState,
 onAction = onAction
 )
 }
 }
 }
}

@Composable
fun LoginContent(
 uiState: UiState,
 onAction: (UiAction) -> Unit
) {
 val scrollState = rememberScrollState()
 val focusManager = LocalFocusManager.current
 val passwordFocusRequester = remember { FocusRequester() }

 Column(
 modifier = Modifier
 .fillMaxSize()
 .verticalScroll(scrollState)
 .padding(24.dp),
 horizontalAlignment = Alignment.CenterHorizontally,
 verticalArrangement = Arrangement.Center
 ) {
 Spacer(modifier = Modifier.height(32.dp))

 // App Logo/Icon
 Card(
 modifier = Modifier
 .size(120.dp)
 .padding(bottom = 32.dp),
 shape = RoundedCornerShape(60.dp),
 colors = CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.primaryContainer
 ),
 elevation = CardDefaults.cardElevation(defaultElevation = 8.dp)
 ) {
 Box(
 modifier = Modifier.fillMaxSize(),
 contentAlignment = Alignment.Center
 ) {
 Text(
 text = "",
 fontSize = 48.sp
 )
 }
 }

 // Welcome Text
 Text(
 text = "Welcome Back!",
 style = MaterialTheme.typography.headlineMedium,
 fontWeight = FontWeight.Bold,
 color = MaterialTheme.colorScheme.onSurface,
 modifier = Modifier.padding(bottom = 8.dp)
 )

 Text(
 text = "Sign in to access your smart fridge",
 style = MaterialTheme.typography.bodyLarge,
 color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.7f),
 textAlign = TextAlign.Center,
 modifier = Modifier.padding(bottom = 32.dp)
 )

 // Login Form Card
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(bottom = 24.dp),
 shape = RoundedCornerShape(24.dp),
 colors = CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.surface
 ),
 elevation = CardDefaults.cardElevation(defaultElevation = 8.dp)
 ) {
 Column(
 modifier = Modifier.padding(24.dp),
 verticalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 // Email Field
 OutlinedTextField(
 value = uiState.email,
 onValueChange = { onAction(UiAction.EmailChanged(it)) },
 label = { Text("Email") },
 leadingIcon = {
 Icon(
 imageVector = Icons.Default.Email,
 contentDescription = "Email Icon"
 )
 },
 modifier = Modifier.fillMaxWidth(),
 keyboardOptions = KeyboardOptions(
 keyboardType = KeyboardType.Email,
 imeAction = ImeAction.Next
 ),
 keyboardActions = KeyboardActions(
 onNext = { passwordFocusRequester.requestFocus() }
 ),
 isError = uiState.isEmailError,
 supportingText = if (uiState.isEmailError) {
 { Text(uiState.emailErrorMessage) }
 } else null,
 shape = RoundedCornerShape(16.dp)
 )

 // Password Field
 OutlinedTextField(
 value = uiState.password,
 onValueChange = { onAction(UiAction.PasswordChanged(it)) },
 label = { Text("Password") },
 leadingIcon = {
 Icon(
 imageVector = Icons.Default.Lock,
 contentDescription = "Password Icon"
 )
 },
 trailingIcon = {
 TextButton(
 onClick = { onAction(UiAction.TogglePasswordVisibility) }
 ) {
 Text(
 text = if (uiState.isPasswordVisible) "Hide" else "Show",
 color = MaterialTheme.colorScheme.primary
 )
 }
 },
 visualTransformation = if (uiState.isPasswordVisible)
 VisualTransformation.None
 else
 PasswordVisualTransformation(),
 modifier = Modifier
 .fillMaxWidth()
 .focusRequester(passwordFocusRequester),
 keyboardOptions = KeyboardOptions(
 keyboardType = KeyboardType.Password,
 imeAction = ImeAction.Done
 ),
 keyboardActions = KeyboardActions(
 onDone = {
 focusManager.clearFocus()
 if (uiState.isLoginEnabled) {
 onAction(UiAction.LoginClicked)
 }
 }
 ),
 isError = uiState.isPasswordError,
 supportingText = if (uiState.isPasswordError) {
 { Text(uiState.passwordErrorMessage) }
 } else null,
 shape = RoundedCornerShape(16.dp)
 )

 // Forgot Password
 Text(
 text = "Forgot Password?",
 color = MaterialTheme.colorScheme.primary,
 style = MaterialTheme.typography.bodyMedium,
 modifier = Modifier
 .align(Alignment.End)
 .clickable { onAction(UiAction.ForgotPasswordClicked) }
 .padding(vertical = 4.dp)
 )

 Spacer(modifier = Modifier.height(8.dp))

 // Login Button
 Button(
 onClick = { onAction(UiAction.LoginClicked) },
 enabled = uiState.isLoginEnabled,
 modifier = Modifier
 .fillMaxWidth()
 .height(56.dp),
 shape = RoundedCornerShape(16.dp)
 ) {
 Text(
 text = "Sign In",
 fontSize = 16.sp,
 fontWeight = FontWeight.SemiBold
 )
 }
 }
 }

 // Sign Up Option
 Row(
 horizontalArrangement = Arrangement.Center,
 verticalAlignment = Alignment.CenterVertically
 ) {
 Text(
 text = "Don't have an account? ",
 color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.7f),
 style = MaterialTheme.typography.bodyMedium
 )
 Text(
 text = "Sign Up",
 color = MaterialTheme.colorScheme.primary,
 style = MaterialTheme.typography.bodyMedium,
 fontWeight = FontWeight.SemiBold,
 modifier = Modifier.clickable { onAction(UiAction.SignUpClicked) }
 )
 }

 Spacer(modifier = Modifier.height(32.dp))
 }
}

