import dagger.hilt.android.qualifiers.ApplicationContext
import okhttp3.MediaType.Companion.toMediaTypeOrNull
import okhttp3.MultipartBody
import okhttp3.RequestBody.Companion.asRequestBody
import java.io.File
import java.io.FileOutputStream
import javax.inject.Inject

class MainRepositoryImpl @Inject constructor(
 private val mainService: MainService,
 private val authService: AuthService,
 private val fridgeService: FridgeService,
 private val userPreferences: UserPreferences,
 @ApplicationContext private val context: Context
) : MainRepository {

 override suspend fun pingServer(): Result<String> {
 return try {
 val response = mainService.ping()
 if (response.isSuccessful) {
 val responseText = response.body()?.string() ?: "pong"
 Result.success(responseText)
 } else {
 Result.failure(Exception("Ping failed: ${response.message()}"))
 }
 } catch (e: Exception) {
 Result.failure(e)
 }
 }

 override suspend fun uploadReceiptImage(imageUri: Uri): Result<String> {
 return try {
 // Convert URI to file
 val inputStream = context.contentResolver.openInputStream(imageUri)
 val file = File(context.cacheDir, "receipt_${System.currentTimeMillis()}.jpg")

 inputStream?.use { input ->
 FileOutputStream(file).use { output ->
 input.copyTo(output)
 }
 }

 // Create multipart body
 val requestBody = file.asRequestBody("image/jpeg".toMediaTypeOrNull())
 val imagePart = MultipartBody.Part.createFormData("image", file.name, requestBody)

 // Upload to server
 val response = mainService.uploadImage(imagePart)

 // Clean up temporary file
 file.delete()

 if (response.isSuccessful) {
 val responseBody = response.body()
 Result.success(responseBody?.message ?: "Image uploaded successfully")
 } else {
 val errorBody = response.errorBody()?.string()
 Result.failure(Exception("Upload failed: $errorBody"))
 }
 } catch (e: Exception) {
 Result.failure(e)
 }
 }

 override suspend fun registerUser(registerRequest: RegisterRequest): Result<String> {
 return try {
 println("DEBUG: Sending registration request to auth service")
 println("DEBUG: Request data: $registerRequest")

 val response = authService.registerUserRaw(registerRequest)

 println("DEBUG: Response code: ${response.code()}")
 println("DEBUG: Response message: ${response.message()}")

 if (response.isSuccessful) {
 val responseBody = response.body()?.string() ?: "Registration successful"
 println("DEBUG: Success response body: $responseBody")
 Result.success(responseBody)
 } else {
 val errorBody = response.errorBody()?.string() ?: ""
 println("DEBUG: Error response body: $errorBody")
 println("DEBUG: Response headers: ${response.headers()}")
 Result.failure(Exception("Registration failed: $errorBody"))
 }
 } catch (e: Exception) {
 println("DEBUG: Exception during registration: ${e.message}")
 println("DEBUG: Exception type: ${e.javaClass.simpleName}")
 e.printStackTrace()
 Result.failure(e)
 }
 }

 override suspend fun loginUser(loginRequest: LoginRequest): Result<String> {
 return try {
 val response = authService.loginUserRaw(loginRequest)
 if (response.isSuccessful) {
 val responseBody = response.body()?.string() ?: ""
 println("DEBUG: Login response: $responseBody")

 // Try to extract userId from response (assuming it's in the response)
 // You may need to adjust this based on your actual server response format
 try {
 val userId = responseBody.toIntOrNull()
 userId?.let { userPreferences.saveUserId(it) }
 } catch (e: Exception) {
 println("DEBUG: Could not extract userId from response: $responseBody")
 }

 Result.success("Login successful")
 } else {
 val errorBody = response.errorBody()?.string()
 Result.failure(Exception("Login failed: $errorBody"))
 }
 } catch (e: Exception) {
 Result.failure(e)
 }
 }

 override suspend fun getFridgeItems(userId: Int): Result<List<FridgeItem>> {
 return try {
 val response = fridgeService.getFridgeItems(userId)
 if (response.isSuccessful) {
 val fridgeItems = response.body() ?: emptyList()
 Result.success(fridgeItems)
 } else {
 val errorBody = response.errorBody()?.string()
 Result.failure(Exception("Failed to get fridge items: $errorBody"))
 }
 } catch (e: Exception) {
 Result.failure(e)
 }
 }
}

