@Composable
fun LoginScreen(
 uiState: UiState,
 uiEffect: Flow<UiEffect>,
 onAction: (UiAction) -> Unit,
 onNavigateToHome: () -> Unit = {},
 onNavigateToSignUp: () -> Unit = {},
 onNavigateToForgotPassword: () -> Unit = {},
) {
 var isVisible by remember { mutableStateOf(false) }
 val snackbarHostState = remember { SnackbarHostState() }

 LaunchedEffect(Unit) {
 delay(100)
 isVisible = true
 }

 // Handle UI effects
 LaunchedEffect(uiEffect) {
 uiEffect.collect { effect ->
 when (effect) {
 is UiEffect.NavigateToHome -> onNavigateToHome()
 is UiEffect.NavigateToSignUp -> onNavigateToSignUp()
 is UiEffect.NavigateToForgotPassword -> onNavigateToForgotPassword()
 is UiEffect.ShowError -> {
 snackbarHostState.showSnackbar(
 message = effect.message,
 duration = SnackbarDuration.Long
 )
 }
 }
 }
 }

 Box(
 modifier = Modifier
 .fillMaxSize()
 .background(
 brush = Brush.verticalGradient(
 colors = listOf(
 MaterialTheme.colorScheme.primaryContainer.copy(alpha = 0.3f),
 MaterialTheme.colorScheme.surface
 )
 )
 )
 ) {
 if (uiState.isLoading) {
 LoadingBar()
 } else {
 AnimatedVisibility(
 visible = isVisible,
 enter = fadeIn(animationSpec = tween(800)) + slideInVertically(
 animationSpec = spring(
 dampingRatio = Spring.DampingRatioMediumBouncy,
 stiffness = Spring.StiffnessLow
 ),
 initialOffsetY = { it / 2 }
 ),
 exit = fadeOut(animationSpec = tween(400)) + slideOutVertically()
 ) {
 LoginContent(
 uiState = uiState,
 onAction = onAction
 )
 }
 }

 // Snackbar for error messages
 SnackbarHost(
 hostState = snackbarHostState,
 modifier = Modifier.align(Alignment.BottomCenter)
 )
 }
}

