 when (effect) {
 is UiEffect.NavigateToHome -> onNavigateToHome()
 is UiEffect.NavigateToSignUp -> onNavigateToSignUp()
 is UiEffect.NavigateToForgotPassword -> onNavigateToForgotPassword()
 is UiEffect.ShowError -> {
 snackbarHostState.showSnackbar(
 message = effect.message,
 duration = SnackbarDuration.Long
 )
 }
 is UiEffect.ShowLoginResponse -> {
 loginResponse = effect.response
 showResponseDialog = true
 }
 }
 }
 }

 Box(
 modifier = Modifier
 .fillMaxSize()
 .background(
 brush = Brush.verticalGradient(
 colors = listOf(
 MaterialTheme.colorScheme.primaryContainer.copy(alpha = 0.3f),
 MaterialTheme.colorScheme.surface
 )
 )
 )
 ) {
 if (uiState.isLoading) {
 LoadingBar()
 } else {
 AnimatedVisibility(
 visible = isVisible,
 enter = fadeIn(animationSpec = tween(800)) + slideInVertically(
 animationSpec = spring(
 dampingRatio = Spring.DampingRatioMediumBouncy,
 stiffness = Spring.StiffnessLow
 ),
 initialOffsetY = { it / 2 }
 ),
 exit = fadeOut(animationSpec = tween(400)) + slideOutVertically()
 ) {
 LoginContent(
 uiState = uiState,
 onAction = onAction
 )
 }
 }

 // Snackbar for error messages
 SnackbarHost(
 hostState = snackbarHostState,
 modifier = Modifier.align(Alignment.BottomCenter)
 )
 }

 // Login Response Dialog
 if (showResponseDialog) {
 AlertDialog(
 onDismissRequest = { showResponseDialog = false },
 title = { Text("Login Response from API") },
 text = {
 Column {
 Text("Raw response from http://localhost:8084/api/auth/login:")
 Spacer(modifier = Modifier.height(8.dp))
 Text(
 text = loginResponse,
 style = MaterialTheme.typography.bodyMedium,
 modifier = Modifier
 .background(
 MaterialTheme.colorScheme.surfaceVariant,
 RoundedCornerShape(8.dp)
 )
 .padding(12.dp)
 )
 }
 },
 confirmButton = {
 TextButton(
 onClick = { showResponseDialog = false }
 ) {
 Text("OK")
 }
 }
 )
 }
}

