```kotlin:app/src/main/java/com/example/my_fridge_android/data/config/ServerConfig.kt, lines=ALL(1-62)
package com.example.my_fridge_android.data.config

import android.content.Context
import android.util.Log
import dagger.hilt.android.qualifiers.ApplicationContext
import javax.inject.Inject
import javax.inject.Singleton

@Singleton
class ServerConfig @Inject constructor(
 @ApplicationContext private val context: Context
) {

 val baseUrl: String by lazy {
 loadServerConfig().ocrServerUrl
 }

 val authServerUrl: String by lazy {
 loadServerConfig().authServerUrl
 }

 private fun loadServerConfig(): ServerUrls {
 return try {
 Log.d("ServerConfig", "Loading server config from assets...")

 val configContent = context.assets.open("server_config.txt").use { inputStream ->
 inputStream.bufferedReader().readLines()
 }
 Log.d("ServerConfig", "Loaded server config lines: $configContent")

 val ocrUrl = if (configContent.isNotEmpty() && configContent[0].isNotBlank()) {
 "http://${configContent[0].trim()}/"
 } else {
 "http://192.168.1.100:5000/"
 }

 val authUrl = if (configContent.size > 1 && configContent[1].isNotBlank()) {
 "http://${configContent[1].trim()}/"
 } else {
 "http://localhost:8084/api/"
 }

 Log.d("ServerConfig", "OCR Server URL: $ocrUrl")
 Log.d("ServerConfig", "Auth Server URL: $authUrl")

 ServerUrls(ocrUrl, authUrl)

 } catch (e: Exception) {
 Log.e("ServerConfig", "Error loading server config", e)
 val fallbackOcr = "http://192.168.1.100:5000/"
 val fallbackAuth = "http://localhost:8084/api/"
 Log.d("ServerConfig", "Using fallback - OCR: $fallbackOcr, Auth: $fallbackAuth")
 ServerUrls(fallbackOcr, fallbackAuth)
 }
 }

 private data class ServerUrls(
 val ocrServerUrl: String,
 val authServerUrl: String
 )
}

