@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun AddIngredientsBottomSheet(
 onDismiss: () -> Unit,
 onAddIngredient: (String, String, String) -> Unit
) {
 val bottomSheetState = rememberModalBottomSheetState(
 skipPartiallyExpanded = true
 )

 var ingredientName by remember { mutableStateOf("") }
 var amount by remember { mutableStateOf("") }
 var selectedAmountType by remember { mutableStateOf("G") }
 var showDropdown by remember { mutableStateOf(false) }

 val amountTypes = listOf("L", "ml", "KG", "G", "amount")

 ModalBottomSheet(
 onDismissRequest = onDismiss,
 sheetState = bottomSheetState,
 dragHandle = { BottomSheetDefaults.DragHandle() },
 containerColor = MaterialTheme.colorScheme.surface,
 contentColor = MaterialTheme.colorScheme.onSurface
 ) {
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 .padding(bottom = 32.dp),
 verticalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 Text(
 text = "Add Ingredient",
 fontSize = 24.sp,
 fontWeight = FontWeight.Bold,
 color = MaterialTheme.colorScheme.onSurface,
 modifier = Modifier.padding(bottom = 8.dp)
 )

 OutlinedTextField(
 value = ingredientName,
 onValueChange = { ingredientName = it },
 label = { Text("Ingredient Name") },
 modifier = Modifier.fillMaxWidth(),
 singleLine = true
 )

 OutlinedTextField(
 value = amount,
 onValueChange = { amount = it },
 label = { Text("Amount") },
 modifier = Modifier.fillMaxWidth(),
 keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Number),
 singleLine = true
 )

 ExposedDropdownMenuBox(
 expanded = showDropdown,
 onExpandedChange = { showDropdown = !showDropdown },
 modifier = Modifier.fillMaxWidth()
 ) {
 OutlinedTextField(
 value = selectedAmountType,
 onValueChange = {},
 readOnly = true,
 label = { Text("Amount Type") },
 trailingIcon = {
 Icon(
 imageVector = Icons.Default.KeyboardArrowDown,
 contentDescription = "Dropdown"
 )
 },
 modifier = Modifier
 .fillMaxWidth()
 .menuAnchor()
 )

 ExposedDropdownMenu(
 expanded = showDropdown,
 onDismissRequest = { showDropdown = false }
 ) {
 amountTypes.forEach { type ->
 DropdownMenuItem(
 text = { Text(type) },
 onClick = {
 selectedAmountType = type
 showDropdown = false
 }
 )
 }
 }
 }

 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 OutlinedButton(
 onClick = onDismiss,
 modifier = Modifier.weight(1f)
 ) {
 Text("Cancel")
 }

 Button(
 onClick = {
 if (ingredientName.isNotBlank() && amount.isNotBlank()) {
 onAddIngredient(ingredientName, amount, selectedAmountType)
 onDismiss()
 }
 },
 modifier = Modifier.weight(1f),
 enabled = ingredientName.isNotBlank() && amount.isNotBlank()
 ) {
 Text("Add")
 }
 }
 }
 }
}

