package com.example.my_fridge_android.data.source.remote

import retrofit2.Response
import retrofit2.http.Body
import retrofit2.http.DELETE
import retrofit2.http.GET
import retrofit2.http.POST
import retrofit2.http.Path
import retrofit2.http.Query
import okhttp3.ResponseBody

interface FridgeService {
 @GET("llm/fridge/{userId}")
 suspend fun getFridgeItems(
 @Path("userId") userId: Int
 ): Response<List<FridgeItem>>
}

interface RecipeAssistantService {
 @POST("tarif_oner")
 suspend fun sendItemsToRecipeAssistant(
 @Body items: List<FridgeItem>
 ): Response<ResponseBody>

 @POST("tarif_detay")
 suspend fun sendRecipeSelection(
 @Body request: RecipeSelectionRequest
 ): Response<ResponseBody>
}

interface DeleteApiService {
 @DELETE("products/delete")
 suspend fun deleteProduct(
 @Query("userId") userId: Int,
 @Query("productName") productName: String
 ): Response<ResponseBody>
}

interface ReduceAmountApiService {
 @POST("fridge/eksilt")
 suspend fun reduceAmount(
 @Query("userId") userId: Int,
 @Query("productName") productName: String,
 @Query("miktar") amount: Int,
 @Query("miktarTuru") amountType: String
 ): Response<ResponseBody>

 @POST("fridge/add")
 suspend fun addAmount(
 @Body request: AddAmountRequest
 ): Response<ResponseBody>
}

data class AddAmountRequest(
 val productName: String,
 val categoryName: String,
 val countType: String,
 val price: Double,
 val amount: Int,
 val skt: String,
 val userId: Int
)

data class FridgeItem(
 val id: Int? = null,
 val userId: Int? = null,
 val amount: Int? = null,
 val eklenmeTarihi: String? = null,
 val skt: String? = null,
 val product: Product? = null
) {
 // Helper function to validate if this item has minimum required data
 fun isValid(): Boolean {
 return id != null &&
 userId != null &&
 amount != null &&
 product != null &&
 product.isValid()
 }

 // Helper function to get safe values with defaults
 fun getSafeId(): Int = id ?: -1
 fun getSafeUserId(): Int = userId ?: -1
 fun getSafeAmount(): Int = amount ?: 0
 fun getSafeEklenmeTarihi(): String = eklenmeTarihi ?: "Unknown date"
 fun getSafeSkt(): String = skt ?: "No expiry date"
 fun getSafeProduct(): Product = product ?: Product.createDefault()
}

data class Product(
 val id: Int? = null,
 val name: String? = null,
 val category: String? = null,
 val price: Double? = null,
 val countType: String? = null
) {
 // Helper function to validate if this product has minimum required data
 fun isValid(): Boolean {
 return !name.isNullOrBlank()
 }

 // Helper functions to get safe values with defaults
 fun getSafeId(): Int = id ?: -1
 fun getSafeName(): String = name ?: "Unknown Product"
 fun getSafeCategory(): String = category ?: "Unknown"
 fun getSafePrice(): Double = price ?: 0.0
 fun getSafeCountType(): String = countType ?: "amount"

 companion object {
 fun createDefault(): Product {
 return Product(
 id = -1,
 name = "Unknown Product",
 category = "Unknown",
 price = 0.0,
 countType = "amount"
 )
 }
 }
}

