```
package com.example.my_fridge_android.data.config

import android.content.Context
import android.util.Log
import dagger.hilt.android.qualifiers.ApplicationContext
import javax.inject.Inject
import javax.inject.Singleton

@Singleton
class ServerConfig @Inject constructor(
 @ApplicationContext private val context: Context
) {

 val baseUrl: String by lazy {
 loadServerConfig().ocrServerUrl
 }

 val authServerUrl: String by lazy {
 loadServerConfig().authServerUrl
 }

 val fridgeServerUrl: String by lazy {
 loadServerConfig().fridgeServerUrl
 }

 val recipeAssistantServerUrl: String by lazy {
 loadServerConfig().recipeAssistantServerUrl
 }

 val deleteApiServerUrl: String by lazy {
 loadServerConfig().deleteApiServerUrl
 }

 val reduceAmountApiServerUrl: String by lazy {
 loadServerConfig().reduceAmountApiServerUrl
 }

 private fun loadServerConfig(): ServerUrls {
 return try {
 Log.d("ServerConfig", "Loading server config from assets...")

 val configContent = context.assets.open("server_config.txt").use { inputStream ->
 inputStream.bufferedReader().readLines()
 .filter { line -> line.isNotBlank() && !line.trim().startsWith("#") }
 }
 Log.d("ServerConfig", "Loaded server config lines: $configContent")
 Log.d("ServerConfig", "Total config lines found: ${configContent.size}")

 val ocrUrl = if (configContent.isNotEmpty() && configContent[0].isNotBlank()) {
 val url = "http://${configContent[0].trim()}/"
 Log.d("ServerConfig", "OCR URL loaded from config: $url")
 url
 } else {
 val url = "http://192.168.1.100:5000/"
 Log.d("ServerConfig", "OCR URL using fallback: $url")
 url
 }

 val authUrl = if (configContent.size > 1 && configContent[1].isNotBlank()) {
 val url = "http://${configContent[1].trim()}/"
 Log.d("ServerConfig", "Auth URL loaded from config: $url")
 url
 } else {
 val url = "http://localhost:8084/api/"
 Log.d("ServerConfig", "Auth URL using fallback: $url")
 url
 }

 val fridgeUrl = if (configContent.size > 2 && configContent[2].isNotBlank()) {
 val url = "http://${configContent[2].trim()}/"
 Log.d("ServerConfig", "Fridge URL loaded from config: $url")
 url
 } else {
 val url = "http://localhost:8085/api/"
 Log.d("ServerConfig", "Fridge URL using fallback: $url")
 url
 }

 val recipeAssistantUrl = if (configContent.size > 3 && configContent[3].isNotBlank()) {
 val url = "http://${configContent[3].trim()}/"
 Log.d("ServerConfig", "Recipe Assistant URL loaded from config: $url")
 url
 } else {
 val url = "http://192.168.1.100:5001/"
 Log.d("ServerConfig", "Recipe Assistant URL using fallback: $url")
 url
 }

 val deleteApiUrl = if (configContent.size > 4 && configContent[4].isNotBlank()) {
 val url = "http://${configContent[4].trim()}/"
 Log.d("ServerConfig", "Delete API URL loaded from config: $url")
 Log.d("ServerConfig", "Config line 5 content: '${configContent[4]}'")
 url
 } else {
 val url = "http://localhost:8086/api/"
 Log.d("ServerConfig", "Delete API URL using fallback: $url")
 Log.d("ServerConfig", "Config content size: ${configContent.size}")
 url
 }

 val reduceAmountApiUrl = if (configContent.size > 5 && configContent[5].isNotBlank()) {
 val url = "http://${configContent[5].trim()}/"
 Log.d("ServerConfig", "Reduce Amount API URL loaded from config: $url")
 Log.d("ServerConfig", "Config line 6 content: '${configContent[5]}'")
 url
 } else {
 val url = "http://localhost:8087/api/"
 Log.d("ServerConfig", "Reduce Amount API URL using fallback: $url")
 Log.d("ServerConfig", "Config content size: ${configContent.size}")
 url
 }

 Log.d("ServerConfig", "=== FINAL SERVER CONFIGURATION ===")
 Log.d("ServerConfig", "OCR Server URL: $ocrUrl")
 Log.d("ServerConfig", "Auth Server URL: $authUrl")
 Log.d("ServerConfig", "Fridge Server URL: $fridgeUrl")
 Log.d("ServerConfig", "Recipe Assistant Server URL: $recipeAssistantUrl")
 Log.d("ServerConfig", "Delete API Server URL: $deleteApiUrl")
 Log.d("ServerConfig", "Reduce Amount API Server URL: $reduceAmountApiUrl")
 Log.d("ServerConfig", "=======================================")

 ServerUrls(
 ocrUrl,
 authUrl,
 fridgeUrl,
 recipeAssistantUrl,
 deleteApiUrl,
 reduceAmountApiUrl
 )

 } catch (e: Exception) {
 Log.e("ServerConfig", "Error loading server config", e)
 val fallbackOcr = "http://192.168.1.100:5000/"
 val fallbackAuth = "http://localhost:8084/api/"
 val fallbackFridge = "http://localhost:8085/api/"
 val fallbackRecipeAssistant = "http://192.168.1.100:5001/"
 val fallbackDeleteApi = "http://localhost:8086/api/"
 val fallbackReduceAmountApi = "http://localhost:8087/api/"
 Log.d(
 "ServerConfig",
 "Using fallback - OCR: $fallbackOcr, Auth: $fallbackAuth, Fridge: $fallbackFridge, Recipe Assistant: $fallbackRecipeAssistant, Delete API: $fallbackDeleteApi, Reduce Amount API: $fallbackReduceAmountApi"
 )
 ServerUrls(
 fallbackOcr,
 fallbackAuth,
 fallbackFridge,
 fallbackRecipeAssistant,
 fallbackDeleteApi,
 fallbackReduceAmountApi
 )
 }
 }

 private data class ServerUrls(
 val ocrServerUrl: String,
 val authServerUrl: String,
 val fridgeServerUrl: String,
 val recipeAssistantServerUrl: String,
 val deleteApiServerUrl: String,
 val reduceAmountApiServerUrl: String
