 id = UUID.randomUUID().toString(),
 content = "Here are some recipe suggestions for you:",
 isFromUser = false,
 recipes = recipes
 )

 updateUiState {
 copy(
 messages = messages + botMessage,
 isMessageSending = false
 )
 }

 } catch (e: Exception) {
