@Composable
fun FridgeItemsList(
 fridgeItems: List<FridgeItem>,
 onDeleteItem: (String) -> Unit,
 onIncreaseItem: (Int) -> Unit,
 onDecreaseItem: (Int) -> Unit
) {
 LazyColumn(
 modifier = Modifier
 .fillMaxSize()
 .padding(horizontal = 16.dp),
 verticalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 items(fridgeItems) { item ->
 FridgeItemCard(
 item = item,
 onDelete = { onDeleteItem(item.id.toString()) },
 onIncrease = { onIncreaseItem(item.id) },
 onDecrease = { onDecreaseItem(item.id) }
 )
 }

 item {
 Spacer(modifier = Modifier.height(120.dp)) // Space for FABs
 }
 }
}

@Composable
fun FridgeItemCard(
 item: FridgeItem,
 onDelete: () -> Unit,
 onIncrease: () -> Unit,
 onDecrease: () -> Unit
) {
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .clickable { /* Handle item click if needed */ },
 shape = RoundedCornerShape(12.dp),
 colors = CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.surfaceVariant
 ),
 elevation = CardDefaults.cardElevation(defaultElevation = 4.dp)
 ) {
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 Column(
 modifier = Modifier.weight(1f)
 ) {
 Text(
 text = item.product.name,
 fontSize = 18.sp,
 fontWeight = FontWeight.Medium,
 color = MaterialTheme.colorScheme.onSurface
 )

 Text(
 text = "Amount: ${item.amount} ${item.product.countType}",
 fontSize = 14.sp,
 color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.7f)
 )

 Text(
 text = "Expires: ${item.skt}",
 fontSize = 16.sp,
 fontWeight = FontWeight.Bold,
 color = MaterialTheme.colorScheme.primary
 )
 }

 // Amount control buttons
 Row(
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 IconButton(
 onClick = onDecrease,
 enabled = item.amount > 1
 ) {
 Text(
 text = "-",
 fontSize = 24.sp,
 color = if (item.amount > 1) 
 MaterialTheme.colorScheme.primary 
 else 
 MaterialTheme.colorScheme.onSurface.copy(alpha = 0.4f)
 )
 }

 Text(
 text = "${item.amount}",
 fontSize = 16.sp,
 fontWeight = FontWeight.Medium,
 color = MaterialTheme.colorScheme.onSurface,
 modifier = Modifier.padding(horizontal = 8.dp)
 )

 IconButton(
 onClick = onIncrease
 ) {
 Icon(
 imageVector = Icons.Default.Add,
 contentDescription = "Increase Amount",
 tint = MaterialTheme.colorScheme.primary
 )
 }

 IconButton(
 onClick = onDelete
 ) {
 Icon(
 imageVector = Icons.Default.Delete,
 contentDescription = "Delete Item",
 tint = MaterialTheme.colorScheme.error
 )
 }
 }
 }
 }
