+ onClick = onIncrease,
+ enabled = !isDeleting
+ ) {
+ if (isDeleting) {
+ CircularProgressIndicator(
+ modifier = Modifier.size(16.dp),
+ color = MaterialTheme.colorScheme.primary
+ )
+ } else {
+ Text(
+ text = "+${smartIncrement}",
+ fontSize = 18.sp,
+ color = MaterialTheme.colorScheme.primary
+ )
+ }
+ }
+
+ IconButton(
+ onClick = {
+ println("DEBUG: Delete button clicked for item: ${item.product.name} with id: ${item.id}")
+ onDelete()
+ },
+ enabled = !isDeleting
+ ) {
+ if (isDeleting) {
+ CircularProgressIndicator(
+ modifier = Modifier.size(24.dp),
+ color = MaterialTheme.colorScheme.error
+ )
