+ userId = userPreferences.getUserId(),
+ amount = amount.toIntOrNull() ?: 1,
+ eklenmeTarihi = SimpleDateFormat("yyyy-MM-dd", Locale.getDefault()).format(Date()),
+ skt = "No expiry date",
+ product = com.example.my_fridge_android.data.source.remote.Product(
+ id = System.currentTimeMillis().toInt(),
+ name = name,
+ category = "Manual",
+ price = 0.0,
+ countType = amountType
+ )
+ )
+
+ updateUiState {
+ copy(fridgeItems = fridgeItems + newItem)
+ }
+ }
+ }
