+ }
+ } catch (e: Exception) {
+ println("DEBUG: EXCEPTION in Reduce Amount call: ${e.message}")
+ println("DEBUG: Exception stack trace:")
+ e.printStackTrace()
+ Result.failure(e)
+ }
+ }
+
+ override suspend fun addAmount(
+ userId: Int,
+ productName: String,
+ categoryName: String,
+ countType: String,
+ price: Double,
+ amount: Int,
+ skt: String
+ ): Result<String> {
+ return try {
+ println("DEBUG: ===== ADD AMOUNT API CALL =====")
+ println("DEBUG: Add Amount API Server URL from config: ${serverConfig.reduceAmountApiServerUrl}")
+ println("DEBUG: Adding amount for user $userId")
+ println("DEBUG: Product name: $productName")
+ println("DEBUG: Category name: $categoryName")
+ println("DEBUG: Count type: $countType")
+ println("DEBUG: Price: $price")
+ println("DEBUG: Amount to add: $amount")
+ println("DEBUG: SKT: $skt")
+
+ val request = AddAmountRequest(
+ productName = productName,
+ categoryName = categoryName,
+ countType = countType,
+ price = price,
+ amount = amount,
+ skt = skt,
+ userId = userId
+ )
+
+ println("DEBUG: Request payload: $request")
+ println("DEBUG: Expected full URL: ${serverConfig.reduceAmountApiServerUrl}fridge/add")
+
+ val response = reduceAmountApiService.addAmount(request)
+ println("DEBUG: Response received:")
+ println("DEBUG: - Actual request URL: ${response.raw().request.url}")
+ println("DEBUG: - Response code: ${response.code()}")
+ println("DEBUG: - Response message: ${response.message()}")
+ println("DEBUG: - Response successful: ${response.isSuccessful}")
+
+ if (response.isSuccessful) {
+ val responseBody = response.body()
+ val actualResponseText = responseBody?.let { body ->
+ val bytes = body.bytes()
+ String(bytes, Charsets.UTF_8)
+ } ?: "Amount added successfully"
+ println("DEBUG: SUCCESS - Add amount response: $actualResponseText")
+ Result.success(actualResponseText)
+ } else {
+ val errorBody = response.errorBody()?.string()
+ println("DEBUG: ERROR - Response body: $errorBody")
+ println("DEBUG: ERROR - Response headers: ${response.headers()}")
+ Result.failure(Exception("Add amount failed: HTTP ${response.code()} - $errorBody"))
+ }
+ } catch (e: Exception) {
+ println("DEBUG: EXCEPTION in Add Amount call: ${e.message}")
+ println("DEBUG: Exception stack trace:")
+ e.printStackTrace()
+ Result.failure(e)
+ }
+ }
 }


