 // If ping succeeds, proceed with upload
 uploadImageToServer(imageUri)
 }
 .onFailure { error ->
 println("Ping failed: ${error.message}")
 updateUiState {
 copy(
 isLoading = false,
 uploadStatus = " Connection failed: ${error.message}"
 )
 }
 }
 }
 }

