import javax.inject.Inject

class MainRepositoryImpl @Inject constructor(
 private val mainService: MainService,
 private val authService: AuthService,
 private val fridgeService: FridgeService,
 private val recipeAssistantService: RecipeAssistantService,
 private val reduceAmountApiService: ReduceAmountApiService,
 private val userPreferences: UserPreferences,
 private val serverConfig: ServerConfig,
 private val deleteApiService: DeleteApiService,
 @ApplicationContext private val context: Context
) : MainRepository {

 override suspend fun pingServer(): Result<String> {
 return try {
 val response = mainService.ping()
 if (response.isSuccessful) {
 val responseText = response.body()?.string() ?: "pong"
 Result.success(responseText)
 } else {
 Result.failure(Exception("Ping failed: ${response.message()}"))
 }
 } catch (e: Exception) {
 Result.failure(e)
 }
 }

 override suspend fun uploadReceiptImage(imageUri: Uri): Result<String> {
 return try {
 // Convert URI to file
 val inputStream = context.contentResolver.openInputStream(imageUri)
 val file = File(context.cacheDir, "receipt_${System.currentTimeMillis()}.jpg")

 inputStream?.use { input ->
 FileOutputStream(file).use { output ->
 input.copyTo(output)
 }
 }

 // Create multipart body
 val requestBody = file.asRequestBody("image/jpeg".toMediaTypeOrNull())
 val imagePart = MultipartBody.Part.createFormData("image", file.name, requestBody)

 // Upload to server
 val response = mainService.uploadImage(imagePart)

 // Clean up temporary file
 file.delete()

 if (response.isSuccessful) {
 val responseBody = response.body()
 Result.success(responseBody?.message ?: "Image uploaded successfully")
 } else {
 val errorBody = response.errorBody()?.string()
 Result.failure(Exception("Upload failed: $errorBody"))
 }
 } catch (e: Exception) {
 Result.failure(e)
 }
 }

 override suspend fun registerUser(registerRequest: RegisterRequest): Result<String> {
 return try {
 println("DEBUG: Sending registration request to auth service")
 println("DEBUG: Request data: $registerRequest")

 val response = authService.registerUserRaw(registerRequest)

 println("DEBUG: Response code: ${response.code()}")
 println("DEBUG: Response message: ${response.message()}")

 if (response.isSuccessful) {
 val responseBody = response.body()?.string() ?: "Registration successful"
 println("DEBUG: Success response body: $responseBody")
 Result.success(responseBody)
 } else {
 val errorBody = response.errorBody()?.string() ?: ""
 println("DEBUG: Error response body: $errorBody")
 println("DEBUG: Response headers: ${response.headers()}")
 Result.failure(Exception("Registration failed: $errorBody"))
 }
 } catch (e: Exception) {
 println("DEBUG: Exception during registration: ${e.message}")
 println("DEBUG: Exception type: ${e.javaClass.simpleName}")
 e.printStackTrace()
 Result.failure(e)
 }
 }

 override suspend fun loginUser(loginRequest: LoginRequest): Result<String> {
 return try {
 val response = authService.loginUser(loginRequest)
 if (response.isSuccessful) {
 val loginResponse = response.body()
 println("DEBUG: Login response: $loginResponse")

 // Extract userId from the structured response
 loginResponse?.userId?.let { userId ->
 userPreferences.saveUserId(userId)
 println("DEBUG: UserId saved: $userId")
 }

 // Extract and save username from the structured response
 loginResponse?.username?.let { username ->
 userPreferences.saveUsername(username)
 println("DEBUG: Username saved: $username")
 }

 // Return the full response as JSON string for popup display
 val responseString = "{\n" +
 " \"message\": \"${loginResponse?.message ?: ""}\",\n" +
 " \"token\": \"${loginResponse?.token ?: ""}\",\n" +
 " \"userId\": ${loginResponse?.userId ?: "null"},\n" +
 " \"username\": \"${loginResponse?.username ?: ""}\"\n" +
 "}"
 Result.success(responseString)
 } else {
 val errorBody = response.errorBody()?.string()
 Result.failure(Exception("Login failed: $errorBody"))
 }
 } catch (e: Exception) {
 Result.failure(e)
 }
 }

 override suspend fun getFridgeItems(userId: Int): Result<List<FridgeItem>> {
 return try {
 val response = fridgeService.getFridgeItems(userId)
 if (response.isSuccessful) {
 val fridgeItems = response.body() ?: emptyList()
 Result.success(fridgeItems)
 } else {
 val errorBody = response.errorBody()?.string()
 Result.failure(Exception("Failed to get fridge items: $errorBody"))
 }
 } catch (e: Exception) {
 Result.failure(e)
 }
 }

 override suspend fun sendItemsToRecipeAssistant(items: List<FridgeItem>): Result<String> {
 return try {
 println("DEBUG: ===== RECIPE ASSISTANT API CALL =====")
 println("DEBUG: Sending ${items.size} items to Recipe Assistant")
 println("DEBUG: Base URL should be same as image upload: ${serverConfig.recipeAssistantServerUrl}")
 println("DEBUG: Full URL should be: ${serverConfig.recipeAssistantServerUrl}/tarif_oner")
 println("DEBUG: Items being sent: $items")

 val response = recipeAssistantService.sendItemsToRecipeAssistant(items)

 println("DEBUG: Response received:")
 println("DEBUG: - Response code: ${response.code()}")
 println("DEBUG: - Response message: ${response.message()}")
 println("DEBUG: - Request URL: ${response.raw().request.url}")
 println("DEBUG: - Response successful: ${response.isSuccessful}")

 if (response.isSuccessful) {
 val responseBody = response.body()
 // Ensure proper UTF-8 encoding for Turkish characters
 val actualResponseText = responseBody?.let { body ->
 val bytes = body.bytes()
 String(bytes, Charsets.UTF_8)
 } ?: "No response content"
 println("DEBUG: SUCCESS - Recipe Assistant actual response: $actualResponseText")
 Result.success(actualResponseText)
 } else {
 val errorBody = response.errorBody()?.string()
 println("DEBUG: ERROR - Response body: $errorBody")
 println("DEBUG: ERROR - Response headers: ${response.headers()}")
 Result.failure(Exception("Failed to send items to Recipe Assistant: HTTP ${response.code()} - $errorBody"))
 }
 } catch (e: Exception) {
 println("DEBUG: EXCEPTION in Recipe Assistant call: ${e.message}")
 e.printStackTrace()
 Result.failure(e)
 }
 }

