```kotlin:app/src/main/java/com/example/my_fridge_android/ui/recieptlist/RecieptListViewModel.kt
package com.example.my_fridge_android.ui.recieptlist

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.my_fridge_android.ui.recieptlist.RecieptListContract.UiAction
import com.example.my_fridge_android.ui.recieptlist.RecieptListContract.UiEffect
import com.example.my_fridge_android.ui.recieptlist.RecieptListContract.UiState
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.channels.Channel
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.flow.receiveAsFlow
import kotlinx.coroutines.flow.update
import kotlinx.coroutines.launch
import javax.inject.Inject

@HiltViewModel
class RecieptListViewModel @Inject constructor() : ViewModel() {

 private val _uiState = MutableStateFlow(UiState())
 val uiState: StateFlow<UiState> = _uiState.asStateFlow()

 private val _uiEffect by lazy { Channel<UiEffect>() }
 val uiEffect: Flow<UiEffect> by lazy { _uiEffect.receiveAsFlow() }

 init {
 loadSampleReceipts()
 }

 fun onAction(uiAction: UiAction) {
 when (uiAction) {
 is UiAction.NavigateToHome -> {
 viewModelScope.launch {
 emitUiEffect(UiEffect.NavigateToHome)
 }
 }

 is UiAction.OpenCamera -> {
 viewModelScope.launch {
 emitUiEffect(UiEffect.OpenCamera)
 }
 }

 is UiAction.DeleteReceipt -> {
 deleteReceipt(uiAction.receiptId)
 }
 }
 }

 private fun loadSampleReceipts() {
 val sampleReceipts = listOf(
 Receipt(
 id = "1",
 storeName = "Walmart",
 date = "2024-01-15",
 total = "$45.67"
 ),
 Receipt(
 id = "2",
 storeName = "Target",
 date = "2024-01-14",
 total = "$23.45"
 ),
 Receipt(
 id = "3",
 storeName = "Kroger",
 date = "2024-01-13",
 total = "$78.90"
 )
 )

 updateUiState { copy(receipts = sampleReceipts) }
 }

 private fun deleteReceipt(receiptId: String) {
 updateUiState {
 copy(receipts = receipts.filter { it.id != receiptId })
 }
 }

 private fun updateUiState(block: UiState.() -> UiState) {
 _uiState.update(block)
 }

 private suspend fun emitUiEffect(uiEffect: UiEffect) {
 _uiEffect.send(uiEffect)
 }
}

```
