import com.example.my_fridge_android.ui.recieptlist.RecieptListContract.UiAction
import com.example.my_fridge_android.ui.recieptlist.RecieptListContract.UiEffect
import com.example.my_fridge_android.ui.recieptlist.RecieptListContract.UiState
import kotlinx.coroutines.delay
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.emptyFlow

@Composable
fun RecieptListScreen(
 uiState: UiState,
 uiEffect: Flow<UiEffect>,
 onAction: (UiAction) -> Unit,
) {
 var isVisible by remember { mutableStateOf(false) }

 LaunchedEffect(Unit) {
 delay(150)
 isVisible = true
 }

 Box(
 modifier = Modifier
 .fillMaxSize()
 .background(
 brush = Brush.verticalGradient(
 colors = listOf(
 MaterialTheme.colorScheme.primaryContainer.copy(alpha = 0.2f),
 MaterialTheme.colorScheme.surface
 )
 )
 )
 ) {
 Column(
 modifier = Modifier.fillMaxSize()
 ) {
 // Top bar with back button
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 IconButton(
 onClick = { onAction(UiAction.NavigateToHome) },
 modifier = Modifier
 .clip(CircleShape)
 .background(MaterialTheme.colorScheme.primaryContainer)
 ) {
 Icon(
 imageVector = Icons.Default.ArrowBack,
 contentDescription = "Back to Home",
 tint = MaterialTheme.colorScheme.onPrimaryContainer
 )
 }

 Spacer(modifier = Modifier.width(16.dp))

 Text(
 text = "My Receipts",
 fontSize = 24.sp,
 fontWeight = FontWeight.Bold,
 color = MaterialTheme.colorScheme.onSurface,
 style = MaterialTheme.typography.headlineMedium
 )
 }

 // Content
 when {
 uiState.isLoading -> {
 Box(
 modifier = Modifier.fillMaxSize(),
 contentAlignment = Alignment.Center
 ) {
 LoadingBar()
 }
 }

 uiState.receipts.isEmpty() -> {
 AnimatedVisibility(
 visible = isVisible,
 enter = fadeIn(animationSpec = tween(600)) + slideInVertically(
 animationSpec = spring(
 dampingRatio = Spring.DampingRatioMediumBouncy,
 stiffness = Spring.StiffnessMedium
 ),
 initialOffsetY = { it / 3 }
 ),
 exit = fadeOut(animationSpec = tween(300)) + slideOutVertically()
 ) {
 EmptyReceiptsContent()
 }
 }
 else -> {
 AnimatedVisibility(
 visible = isVisible,
 enter = fadeIn(animationSpec = tween(700)) + slideInVertically(
 animationSpec = spring(
 dampingRatio = Spring.DampingRatioMediumBouncy,
 stiffness = Spring.StiffnessMedium
 ),
 initialOffsetY = { it / 3 }
 ),
 exit = fadeOut(animationSpec = tween(300)) + slideOutVertically()
 ) {
 ReceiptsList(
 receipts = uiState.receipts,
 onDeleteReceipt = { receiptId ->
 onAction(UiAction.DeleteReceipt(receiptId))
 }
 )
 }
 }
 }
 }

 // Floating Action Button for Adding Receipt
 FloatingActionButton(
 onClick = { onAction(UiAction.ShowImagePicker) },
 modifier = Modifier
 .align(Alignment.BottomStart)
 .padding(16.dp),
 containerColor = MaterialTheme.colorScheme.primary,
 contentColor = MaterialTheme.colorScheme.onPrimary
 ) {
 Icon(
 imageVector = Icons.Default.Add,
 contentDescription = "Add Receipt"
 )
 }

 // Image Picker Bottom Sheet
 if (uiState.showImagePickerSheet) {
 ImagePickerBottomSheet(
 onDismiss = { onAction(UiAction.HideImagePicker) },
 onTakePhoto = { onAction(UiAction.TakePhoto) },
 onSelectFromGallery = { onAction(UiAction.SelectFromGallery) }
 )
 }
 }
}

@Composable
fun ImagePickerBottomSheet(
 onDismiss: () -> Unit,
 onTakePhoto: () -> Unit,
 onSelectFromGallery: () -> Unit
) {
 val bottomSheetState = rememberModalBottomSheetState(
 skipPartiallyExpanded = true
 )

 ModalBottomSheet(
 onDismissRequest = onDismiss,
 sheetState = bottomSheetState,
 dragHandle = { BottomSheetDefaults.DragHandle() },
 containerColor = MaterialTheme.colorScheme.surface,
 contentColor = MaterialTheme.colorScheme.onSurface
 ) {
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 .padding(bottom = 32.dp),
 verticalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 // Title
 Text(
 text = "Add Receipt",
 fontSize = 24.sp,
 fontWeight = FontWeight.Bold,
 color = MaterialTheme.colorScheme.onSurface,
 modifier = Modifier.padding(bottom = 8.dp)
 )

 Text(
 text = "Choose how you'd like to add your receipt",
 fontSize = 16.sp,
 color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.7f),
 modifier = Modifier.padding(bottom = 16.dp)
 )

 // Take Photo Option
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .clickable { 
 onTakePhoto()
 onDismiss()
 },
 shape = RoundedCornerShape(16.dp),
 colors = CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.primaryContainer
 ),
 elevation = CardDefaults.cardElevation(defaultElevation = 2.dp)
 ) {
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(20.dp),
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 Icon(
 imageVector = Icons.Default.CameraAlt,
 contentDescription = "Take Photo",
 tint = MaterialTheme.colorScheme.onPrimaryContainer,
 modifier = Modifier.size(32.dp)
 )

 Column(
 modifier = Modifier.weight(1f)
 ) {
 Text(
 text = "Take Photo",
 fontSize = 18.sp,
 fontWeight = FontWeight.SemiBold,
 color = MaterialTheme.colorScheme.onPrimaryContainer
 )
 Text(
 text = "Use camera to capture receipt",
 fontSize = 14.sp,
 color = MaterialTheme.colorScheme.onPrimaryContainer.copy(alpha = 0.7f)
 )
 }
 }
 }

 // Select from Gallery Option
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .clickable { 
 onSelectFromGallery()
 onDismiss()
 },
 shape = RoundedCornerShape(16.dp),
 colors = CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.secondaryContainer
 ),
 elevation = CardDefaults.cardElevation(defaultElevation = 2.dp)
 ) {
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(20.dp),
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 Icon(
 imageVector = Icons.Default.PhotoLibrary,
 contentDescription = "Select from Gallery",
 tint = MaterialTheme.colorScheme.onSecondaryContainer,
 modifier = Modifier.size(32.dp)
 )

 Column(
 modifier = Modifier.weight(1f)
 ) {
 Text(
 text = "Choose from Gallery",
 fontSize = 18.sp,
 fontWeight = FontWeight.SemiBold,
 color = MaterialTheme.colorScheme.onSecondaryContainer
 )
 Text(
 text = "Select existing photo from gallery",
 fontSize = 14.sp,
 color = MaterialTheme.colorScheme.onSecondaryContainer.copy(alpha = 0.7f)
 )
 }
 }
 }
 }
 }
}

@Composable
fun EmptyReceiptsContent() {
 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(32.dp),
 verticalArrangement = Arrangement.Center,
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Text(
 text = "",
 fontSize = 120.sp
 )

 Spacer(modifier = Modifier.height(16.dp))

 Text(
 text = "No receipts yet",
 fontSize = 24.sp,
 fontWeight = FontWeight.Medium,
 color = MaterialTheme.colorScheme.onSurface
 )

 Spacer(modifier = Modifier.height(8.dp))

 Text(
 text = "Tap the + button to add your first receipt",
 fontSize = 16.sp,
 color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.7f)
 )
 }
}

@Composable
fun ReceiptsList(
 receipts: List<Receipt>,
 onDeleteReceipt: (String) -> Unit
) {
 LazyColumn(
 modifier = Modifier
 .fillMaxSize()
 .padding(horizontal = 16.dp),
 verticalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 items(receipts) { receipt ->
 ReceiptItem(
 receipt = receipt,
 onDelete = { onDeleteReceipt(receipt.id) }
 )
 }

 item {
 Spacer(modifier = Modifier.height(80.dp)) // Space for FAB
 }
 }
}

@Composable
fun ReceiptItem(
 receipt: Receipt,
 onDelete: () -> Unit
) {
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .clickable { /* Handle receipt click if needed */ },
 shape = RoundedCornerShape(12.dp),
 colors = CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.surfaceVariant
 ),
 elevation = CardDefaults.cardElevation(defaultElevation = 4.dp)
 ) {
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 Text(
 text = "",
 fontSize = 40.sp
 )

 Spacer(modifier = Modifier.width(16.dp))

 Column(
 modifier = Modifier.weight(1f)
 ) {
 Text(
 text = receipt.storeName,
 fontSize = 18.sp,
 fontWeight = FontWeight.Medium,
 color = MaterialTheme.colorScheme.onSurface
 )

 Text(
 text = receipt.date,
 fontSize = 14.sp,
 color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.7f)
 )

 Text(
 text = receipt.total,
 fontSize = 16.sp,
 fontWeight = FontWeight.Bold,
 color = MaterialTheme.colorScheme.primary
 )
 }

 IconButton(
 onClick = onDelete
 ) {
 Icon(
 imageVector = Icons.Default.Delete,
 contentDescription = "Delete Receipt",
 tint = MaterialTheme.colorScheme.error
 )
 }
 }
 }
}

