@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun RecieptListScreen(
 uiState: UiState,
 uiEffect: Flow<UiEffect>,
 onAction: (UiAction) -> Unit,
) {
 var isVisible by remember { mutableStateOf(false) }

 LaunchedEffect(Unit) {
 delay(150)
 isVisible = true
 }

 Box(
 modifier = Modifier
 .fillMaxSize()
 .background(
 brush = Brush.verticalGradient(
 colors = listOf(
 MaterialTheme.colorScheme.primaryContainer.copy(alpha = 0.2f),
 MaterialTheme.colorScheme.surface
 )
 )
 )
 ) {
 Column(
 modifier = Modifier.fillMaxSize()
 ) {
 // Top bar with back button
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 IconButton(
 onClick = { onAction(UiAction.NavigateToHome) },
 modifier = Modifier
 .clip(CircleShape)
 .background(MaterialTheme.colorScheme.primaryContainer)
 ) {
 Icon(
 imageVector = Icons.Default.ArrowBack,
 contentDescription = "Back to Home",
 tint = MaterialTheme.colorScheme.onPrimaryContainer
 )
 }

 Spacer(modifier = Modifier.width(16.dp))

 Text(
 text = "My Receipts",
 fontSize = 24.sp,
 fontWeight = FontWeight.Bold,
 color = MaterialTheme.colorScheme.onSurface,
 style = MaterialTheme.typography.headlineMedium
 )
 }

 // Content
 when {
 uiState.isLoading -> {
 Box(
 modifier = Modifier.fillMaxSize(),
 contentAlignment = Alignment.Center
 ) {
 LoadingBar()
 }
 }

 uiState.receipts.isEmpty() -> {
 AnimatedVisibility(
 visible = isVisible,
 enter = fadeIn(animationSpec = tween(600)) + slideInVertically(
 animationSpec = spring(
 dampingRatio = Spring.DampingRatioMediumBouncy,
 stiffness = Spring.StiffnessMedium
 ),
 initialOffsetY = { it / 3 }
 ),
 exit = fadeOut(animationSpec = tween(300)) + slideOutVertically()
 ) {
 EmptyReceiptsContent()
 }
 }
 else -> {
 AnimatedVisibility(
 visible = isVisible,
 enter = fadeIn(animationSpec = tween(700)) + slideInVertically(
 animationSpec = spring(
 dampingRatio = Spring.DampingRatioMediumBouncy,
 stiffness = Spring.StiffnessMedium
 ),
 initialOffsetY = { it / 3 }
 ),
 exit = fadeOut(animationSpec = tween(300)) + slideOutVertically()
 ) {
 ReceiptsList(
 receipts = uiState.receipts,
 onDeleteReceipt = { receiptId ->
 onAction(UiAction.DeleteReceipt(receiptId))
 }
 )
 }
 }
 }
 }

 // Floating Action Button for Adding Receipt
 FloatingActionButton(
 onClick = { onAction(UiAction.ShowImagePicker) },
 modifier = Modifier
 .align(Alignment.BottomStart)
 .padding(16.dp),
 containerColor = MaterialTheme.colorScheme.primary,
 contentColor = MaterialTheme.colorScheme.onPrimary
 ) {
 Icon(
 imageVector = Icons.Default.Add,
 contentDescription = "Add Receipt"
 )
 }

 // Image Picker Bottom Sheet
 if (uiState.showImagePickerSheet) {
 ImagePickerBottomSheet(
 onDismiss = { onAction(UiAction.HideImagePicker) },
 onTakePhoto = { onAction(UiAction.TakePhoto) },
 onSelectFromGallery = { onAction(UiAction.SelectFromGallery) }
 )
 }
 }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun ImagePickerBottomSheet(
 onDismiss: () -> Unit,
 onTakePhoto: () -> Unit,
 onSelectFromGallery: () -> Unit
) {
 val bottomSheetState = rememberModalBottomSheetState(
 skipPartiallyExpanded = true
 )

 ModalBottomSheet(
 onDismissRequest = onDismiss,
 sheetState = bottomSheetState,
 dragHandle = { BottomSheetDefaults.DragHandle() },
 containerColor = MaterialTheme.colorScheme.surface,
 contentColor = MaterialTheme.colorScheme.onSurface
 ) {
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 .padding(bottom = 32.dp),
